import React ,{Component} from "react";
import {Link} from "react-router-dom";
class OneMonthTracker extends Component{

    Months= ["January","February","March","April","May","June","July","August","September","October","November","December"];

    state = {
        Type : this.props.match.params.Type,
        SubTotal : 0
    }

    OnClickMonth = event =>{
        console.log(event.target.value);
    }
   

    render(){
        const MonthBtn = {
            borderRadius: "8px",
            backgroundColor: "green",
            color: "white",
            border: "1px green solid",
            margin: "10px",
            padding: "5px"
        }

        const BtnBack = {
            backgroundColor: "#F7BA43",
            border: "#F7BA43 1px solid",
            borderRadius:"10px",
            fontSize: "20px",
            margin: "5px",
        }
        const LabelStyle={
            color: "white",
            fontSize: "20px"
           }

        return(
            <div className="container">
                <div className="row">
                    <h1>{this.state.Type}</h1>
                </div>
                <div className="row">
                    {
                        this.Months.map((month,index) =>{
                            return (
                                <div className="col-3 col-xl-1" key={index}>
                                    <button  style={MonthBtn}> {month.slice(0,3)}</button>
                                </div>
                            )
                        })
                    }
                </div>
                <div className="row mt-4 mb-4">
                    <div className="col" style={{display: "grid"}}>
                        <label style={LabelStyle}>SubTotal</label>
                        <input type="number" readOnly value={this.state.SubTotal}/>
                    </div>
                </div>
                <Link to="/GitTracker">
                    <button style={BtnBack}>
                        Back
                    </button>
                </Link>
            </div>
        );
    }
}

export default OneMonthTracker;
