import React,{ Component } from 'react';
import {BrowserRouter as Rounter , Route , Link} from "react-router-dom";
import GitTracker from "./GitTracker";

 class Home extends Component {

  date = new Date();
  Months= ["January","February","March","April","May","June","July","August","September","October","November","December"];
   
   state = {
     Month: this.Months[this.date.getMonth()+1],
     Amount: 0,
     Category: ""
   };

   getMonth = event => {
    this.setState({ Month: event.target.value });
   };

   getAmount = event => {
     this.setState({ Amount: event.target.value });
   };

   getCategory = event => {
     this.setState({ Category: event.target.value });
     console.log(this.state.Month +  " || " +  this.state.Amount +  "||" +  event.target.value);
     this.resetExpense();
   };

   resetExpense = () => {
     this.setState({
       Amount: 0,
       Category: ""
     });
   };

   setMonth = (event) =>{
      let date = new Date();
      event.target.value = this.state.monthNames[(date.getMonth()+1)];
   }

   render() {
     const DisplayGrid = {
       display: "grid"
     };
     const MarginB = {
       marginBottom: "10px"
     };

     const MainColor = {
       backgroundColor: "#F2958D"
     };
     return (
       <div className="container" style={MainColor}>
         <div className="row pt-2 pb-2">
           <div className="col" style={MarginB}>

             <div style={DisplayGrid}>
              <label> Date </label>
              <input type="text" readOnly  value = {this.state.Month}/>
             </div>
           </div>
           <div className="col" style={MarginB}>
             <div style={DisplayGrid}>
               <label> Amount </label>
               <input
                 type="number"
                 id="Amount"
                 name="Amount"
                 value={this.state.Amount}
                 onChange={this.getAmount.bind(this)}
               />
             </div>
           </div>
           <div className="col" style={MarginB}>
             <div style={DisplayGrid}>
               <label> Category </label>
               <select
                 id="Category"
                 name="Category"
                 value={this.state.Category}
                 onChange={this.getCategory.bind(this)}
               >
                 <option value="selected" value="" />
                 <option value="Housing">Housing</option>
                 <option value="Enteratment">Enteratment</option>
                 <option value="Taxes">Taxes</option>
                 <option value="Repair and Maintenance">
                   Repair and Maintenance
                 </option>
                 <option value="Electricity and Water fees">
                   Electricity and Water fees
                 </option>
               </select>
             </div>
           </div>
         </div>
         <div className="row">
           <div className="col-12">
             <div>
               <Route path="/GitTracker" Component={GitTracker}>
                 <button>Git Tracker</button>
               </Route>
             </div>
           </div>
         </div>
       </div>
     );
   }
 }

export default Home;
